public class question8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int X = 36;
		int Y = 64;
		System.out.println("Intial X value = " + X);
		System.out.println("Intial Y value = " + Y);
		X = X+Y;
		Y = X-Y;
		X = X-Y;
		System.out.println("Final X value = " + X);
		System.out.println("Final Y value = " + Y);
		
	}

}
